mapper.select:
    select 

    from system_user_info

	data_status = #{dataStatus,jdbcType=VARCHAR}
	AND system_user_id != #{noid,jdbcType=INTEGER}
	AND system_user_account = #{systemUserAccount,jdbcType=VARCHAR}
	AND system_department_id = #{systemDepartmentId,jdbcType=INTEGER}

SELECT d.system_center_name AS systemCenterName, c.system_department_name AS systemDepartmentName, b.system_role_name AS systemRoleName, a.system_user_id AS systemUserId, a.system_user_account AS systemUserAccount, a.system_user_type AS systemUserType, a.system_user_password AS systemUserPassword, a.system_user_nickname AS systemUserNickname, a.system_role_id AS systemRoleId, a.last_login_time AS lastLoginTime, a.system_user_accname AS systemUserAccname, a.system_department_id AS systemDepartmentId, a.first_create_by AS firstCreateBy, a.first_create_date AS firstCreateDate, a.data_status AS dataStatus, a.data_separate_id AS dataSeparateId, a.description AS description, a.last_update_by AS lastUpdateBy, a.last_update_date AS lastUpdateDate FROM system_user_info a JOIN system_role_info b ON a.system_role_id = b.system_role_id JOIN system_department_info c ON a.system_department_id = c.system_department_id JOIN system_center_info d ON c.system_center_id = d.system_center_id
a.system_user_id > 100 AND a.data_status = #{dataStatus,jdbcType=VARCHAR} AND a.system_user_account like CONCAT('%', #{systemUserAccount,jdbcType=VARCHAR}, '%') AND a.system_user_nickname like CONCAT('%', #{systemUserNickname,jdbcType=VARCHAR}, '%') AND a.system_role_id = #{systemRoleId,jdbcType=INTEGER} AND a.system_department_id = #{systemDepartmentId,jdbcType=INTEGER}
ORDER BY a.first_create_date DESC
